mapper.select:
SELECT * FROM newMeet_RDS.Member s 
LEFT OUTER JOIN newMeet_RDS.mMember l 
ON s.nidx = l.nidx
WHERE l.m_idx = #{m_idx} 
LIMIT #{index}, #{count}

SELECT * FROM newMeet_RDS.mMember s 
LEFT OUTER JOIN newMeet_RDS.Review l
ON s.nidx = l.nidx 
WHERE s.m_idx = #{m_idx} AND l.nidx = #{nidx}
ORDER BY r_date DESC
LIMIT #{index}, #{count}

SELECT * FROM newMeet_RDS.mMember s 
LEFT OUTER JOIN newMeet_RDS.rComment l 
ON s.nidx = l.nidx
WHERE s.m_idx = #{m_idx} AND l.nidx = #{nidx} 
ORDER BY rd_date DESC
LIMIT #{index}, #{count}

SELECT * FROM newMeet_RDS.mMember s
LEFT OUTER JOIN newMeet_RDS.Meeting l
ON s.m_idx = l.m_idx
WHERE s.nidx = #{nidx} 
ORDER BY l.m_idx DESC
LIMIT #{index}, #{count}
